import React from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import axios from 'axios';

const EditModal = ({ showModal, handleClose, editedText, handleChange, handleSave, isSaveButtonVisible, errorModalMessage, selectedContentId }) => {

  // APIのエンドポイント
  const url = 'https://sample-api.manabupanda.net/api/list';

  // 保存ボタンクリック時のPOSTリクエスト
  const handleSaveClick = async () => {
    try {
      console.log("編集内容をPOSTリクエストで送信します:");
      console.log("送信するデータ: ", {
        id: selectedContentId, // 編集対象のIDを送信
        name: editedText, // 編集されたテキスト
      });

      // POSTリクエストを送信
      const response = await axios.post(url, {
        id: selectedContentId,
        name: editedText, // 編集されたテキストをnameプロパティとして送信
      }, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      console.log("POSTリクエストが成功しました:");
      console.log("レスポンスデータ: ", response.data);

      handleSave(); // 保存処理を呼び出して、画面上のコンテンツも更新
      handleClose(); // モーダルを閉じる

    } catch (error) {
      console.error("POSTリクエストが失敗しました:");
      if (error.response) {
        console.error("サーバーからのエラーレスポンス:", error.response.data);
      } else if (error.request) {
        console.error("サーバーからのレスポンスがありません:", error.request);
      } else {
        console.error("リクエストの設定エラー:", error.message);
      }
    }
  };

  // 削除ボタンクリック時のDELETEリクエスト
  const handleDeleteClick = async () => {
    try {
      console.log("削除のDELETEリクエストを送信します。削除するID:", selectedContentId);

      // DELETEリクエストを送信
      const response = await axios.delete(`${url}/${selectedContentId}`);

      console.log("DELETEリクエストが成功しました:");
      console.log("レスポンスデータ: ", response.data);

      handleClose(); // モーダルを閉じる
    } catch (error) {
      console.error("DELETEリクエストが失敗しました:");
      if (error.response) {
        console.error("サーバーからのエラーレスポンス:", error.response.data);
      } else if (error.request) {
        console.error("サーバーからのレスポンスがありません:", error.request);
      } else {
        console.error("リクエストの設定エラー:", error.message);
      }
    }
  };

  return (
    <Modal show={showModal} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>編集</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group controlId="formBasicEmail">
            <Form.Label>編集内容</Form.Label>
            <Form.Control
              type="text"
              placeholder="編集する内容を入力"
              value={editedText}
              onChange={handleChange}
            />
            {errorModalMessage && <p className="error-message">{errorModalMessage}</p>}
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          閉じる
        </Button>
        <Button variant="danger" onClick={handleDeleteClick}>
          削除
        </Button>
        {isSaveButtonVisible && (
          <Button variant="primary" onClick={handleSaveClick}>
            保存
          </Button>
        )}
      </Modal.Footer>
    </Modal>
  );
};

export default EditModal;
