import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
import './App.css';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import EditModal from './components/EditModal';
import axios from 'axios';

const url = 'https://sample-api.manabupanda.net/api/list';

function BasicExample() {
  const [contents, setContents] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedContentId, setSelectedContentId] = useState(null);
  const [editedText, setEditedText] = useState('');
  const [addText, setAddText] = useState('');
  const [isSaveButtonVisible, setIsSaveButtonVisible] = useState(true);
  const [errorMessage, setErrorMessage] = useState('');
  const [errorModalMessage, setModalErrorMessage] = useState('');
  const [displayedText, setDisplayedText] = useState(''); // 表示用のテキスト状態を追加

  const onClickAdd = async () => {
    if (addText === "") {
      setErrorMessage('文字が未入力です');
      return;
    }

    const id = Date.now();

    try {
      const response = await axios.post(url, {
        id: id,
        name: addText,
      }, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const newContent = {
        id: response.data.id,
        content: (
          <div className="todo-item" key={response.data.id}>
            <div className="todo-text">
              <p className="todo-paragraph">{response.data.name}</p>
            </div>
            <div className="todo-buttons">
              <Button variant="primary" size="sm" onClick={() => handleEdit(response.data.id)}>
                編集
              </Button>{' '}
              <Button onClick={() => handleDelete(response.data.id)} variant="danger" size="sm">
                削除
              </Button>
            </div>
          </div>
        ),
      };

      setContents([...contents, newContent]);
      setDisplayedText(addText); // 新規追加時のテキストを表示用にセット
      setAddText('');
      setErrorMessage('');

    } catch (error) {
      console.error('POSTリクエストエラー:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      const deleteUrl = `${url}/${id}`;
      const response = await axios.delete(deleteUrl);
      setContents(prevContents => prevContents.filter(content => content.id !== id));
    } catch (error) {
      console.error('DELETEリクエストに失敗しました:', error);
    }
  };

  const handleEdit = (id) => {
    setShowModal(true);
    const contentToEdit = contents.find(content => content.id === id);
    if (contentToEdit) {
      setSelectedContentId(id);
      const textToEdit = contentToEdit.content.props.children[0].props.children.props.children;
      setEditedText(textToEdit);
      setIsSaveButtonVisible(true);
      setModalErrorMessage("");
    }
  };

  const handleClose = () => setShowModal(false);

  const handleSave = async () => {
    if (editedText === "") {
      setModalErrorMessage('文字が未入力です');
      return;
    }

    try {
      const response = await axios.post(`${url}/${selectedContentId}`, {
        name: editedText,
      });

      setContents(prevContents =>
        prevContents.map(content =>
          content.id === selectedContentId
            ? {
                ...content,
                content: (
                  <div className="todo-item" key={content.id}>
                    <div className="todo-text">
                      <p className="todo-paragraph">{response.data.name}</p>
                    </div>
                    <div className="todo-buttons">
                      <Button variant="primary" size="sm" onClick={() => handleEdit(content.id)}>
                        編集
                      </Button>{' '}
                      <Button onClick={() => handleDelete(content.id)} variant="danger" size="sm">
                        削除
                      </Button>
                    </div>
                  </div>
                )
              }
            : content
        )
      );

      setDisplayedText(editedText); // 保存した内容を表示用にセット
      handleClose();
    } catch (error) {
      console.error('POSTリクエストに失敗しました:', error);
    }
  };

  const handleAddTextChange = (e) => {
    setAddText(e.target.value);
    if (e.target.value !== "") {
      setErrorMessage('');
    }
  };

  const handleChange = (e) => {
    const newText = e.target.value;
    setEditedText(newText);
    if (newText !== "") {
      setModalErrorMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      onClickAdd();
    }
  };

  return (
    <section>
      <Navbar className="custom-navbar bg-body-tertiary" expand="lg">
        <Container className="navbar-container">
          <Navbar.Brand href="#home">Navbar</Navbar.Brand>
        </Container>
      </Navbar>
      <div className="add-section">
        <div className="input-container">
          <input
            type='text'
            value={addText}
            onChange={handleAddTextChange}
            onKeyPress={handleKeyPress}
            className="add-input"
          />
        </div>
        <Button onClick={onClickAdd} variant="info">新規追加</Button>
      </div>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      <div className="todos-container">
        <div className="todos-header">Todos</div>
        {contents.map((content) => (
          <div key={content.id}>
            {content.content}
          </div>
        ))}
      </div>
      <div>
        {displayedText && <p>表示中の内容: {displayedText}</p>}
      </div>
      <EditModal
        showModal={showModal}
        handleClose={handleClose}
        editedText={editedText}
        handleChange={handleChange}
        handleSave={handleSave}
        isSaveButtonVisible={isSaveButtonVisible}
        errorModalMessage={errorModalMessage}
      />
    </section>
  );
}

export default BasicExample;
