import React from 'react';
import { Modal, Button } from 'react-bootstrap';
import axios from 'axios'; // axiosをインポート

const url = 'https://sample-api.manabupanda.net/api/list'; // APIのベースURL

function EditModal({ showModal, handleClose, editedText, handleChange, handleSave, isSaveButtonVisible, errorModalMessage, selectedContentId, onDeleteSuccess }) {

  // DELETEリクエストを送信する関数
  const handleDeleteClick = async () => {
    try {
      console.log(`ID: ${selectedContentId} の項目を削除します`);
      const deleteUrl = `${url}/${selectedContentId}`;
      console.log(`削除リクエストのURL: ${deleteUrl}`);

      // DELETEリクエストを送信
      const response = await axios.delete(deleteUrl, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      console.log('DELETEリクエスト成功:', response.data);

      // リストから削除された後の処理（親コンポーネントでリストの更新）
      onDeleteSuccess(selectedContentId);

      // モーダルを閉じる
      handleClose();
    } catch (error) {
      console.error('DELETEリクエストに失敗しました:', error);
      if (error.response) {
        console.error('サーバーからのエラーレスポンス:', error.response.data);
      } else if (error.request) {
        console.error('サーバーからのレスポンスがありません:', error.request);
      } else {
        console.error('リクエスト設定のエラー:', error.message);
      }
    }
  };

  return (
    <Modal show={showModal} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>編集</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <input
          type="text"
          value={editedText}
          onChange={handleChange}
          className="form-control"
        />
        {errorModalMessage && <p className="error-message">{errorModalMessage}</p>}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          閉じる
        </Button>
        {isSaveButtonVisible && (
          <Button variant="primary" onClick={handleSave}>
            保存
          </Button>
        )}
        <Button variant="danger" onClick={handleDeleteClick}>
          削除
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

export default EditModal;
